#define TX_PKT_BUF_ACK 15

//RTS/CTS is not currently supported. This threshold must remain larger than any outgoing MPDU
#define RTS_THRESHOLD 2000

//TODO: In a future release, control of these parameters will be made available to CPU_HIGH through IPC commands
#define DCF_CW_EXP_MIN 4
#define DCF_CW_EXP_MAX 10

//CW Update Reasons
#define DCF_CW_UPDATE_MPDU_TX_ERR 0
#define DCF_CW_UPDATE_MPDU_RX_ACK 1
#define DCF_CW_UPDATE_BCAST_TX 2

#define MAC_HW_LASTBYTE_ADDR1 (13)


// IPC_MBOX_LOW_PARAM Commands
#define LOW_PARAM_WORKSHOP_CONFIG                0x02

#define CMD_PARAM_ENABLE_POS                        0
#define CMD_PARAM_FADING_POS                        8
#define CMD_PARAM_DIVERSITY_ORDER_POS              16
#define CMD_PARAM_TX_POWER_POS                     24
#define CMD_PARAM_RETRANS_SWITCH_THRESHOLD_POS     16

#define CMD_PARAM_NODE_TX_POWER_MIN_DBM           -12


// IPC_MBOX_LOW_PARAM Structures
typedef struct{
	u8                  enable;                            // Is antenna enabled
	u8                  fading;                            // Is fading enabled
	u8                  diversity_order;                   // Diversity order of the antenna fading
	s8                  tx_power;                          // Tx power
} antenna_config;

typedef struct{
	antenna_config      ant[2];                             // Antenna configuration [A, B]
} antenna_mode_cfg;

typedef struct{
	u8                  enable;                            // Enable workshop mode
	u8                  switch_thresh;                     // Number of re-transmissions before switching to Mode 2
	                                                       //   NOTE:  Value of 0 indicates, always use Mode 1
	antenna_mode_cfg    mode[2];                           // Mode antenna configuration [0, 1]
} workshop_ant_config;


